import { useState , useRef, useContext} from 'react';
import { useHistory } from 'react-router-dom';
import AuthContext from '../../store/auth-context';
import logo from '../../assets/logo.png';
import classes from './AuthForm.module.css';

const AuthForm = () => {
  const history = useHistory();
  const authCtx = useContext(AuthContext);
  const usernameInputRef = useRef();
  const  passwordInputRef = useRef();
  const [isLogin, setIsLogin] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const sumbitHandler = (event) => {
    event.preventDefault();
    const enteredUsername = usernameInputRef.current.value;
    const enteredPassord = passwordInputRef.current.value;
    setIsLoading(true);
    let url = '';
     if (isLogin) {
        url = 'https://goat-dev.plan4better.de/api/v1/login/access-token'
     } else {
      url = 'https://goat-dev.plan4better.de/api/v1/login/access-token'
     }
     fetch(url,
     {
       method: 'POST',
       body: JSON.stringify({
         username: enteredUsername,
         password : enteredPassord,
         grant_type: '',
         scope: '',
         client_id: '',
         client_secret:''
       }),
       headers: {
        'Content-Type' : 'application/json' 
      }
     }).then(res => {
       setIsLoading(false);
       if (res.ok) {
          return res.json().then(data => {
            console.log(data);
            authCtx.login(data.access_token);
            history.replace('/');
           });
       } else {
         return res.json().then( data => {

          let errorMessage = 'Authentication failed!';
          if ( data && data.error && data.error.message) {
            errorMessage = data?.error?.message;
          }
          throw new Error(errorMessage);

        }).catch(err => {
          alert(err.message);
         });
       }

     });
  };

  return (
    <section className={classes.auth}>

      <img src={logo} alt="Logo" />
      <h2 className={classes.authh2}>Dashboard</h2>
      <h1 className={classes.authh1}>Login</h1>
      <form onSubmit={sumbitHandler}>
        <div className={classes.control}>
          <input className={classes.input}  type='email' id='email' placeholder='Email address' required  ref={usernameInputRef}/>
        </div>
        <div className={classes.control}>
          <input className={classes.input} type='password' id='password' placeholder='Password' required  ref={passwordInputRef}/>
        </div>
        <div className={classes.actions}>
         {!isLoading && <button>{isLogin ? 'Login' : 'Create Account'}</button>}
         {isLoading && <p style={{color: 'white'}}>Loading ..</p>}
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default AuthForm;
